'use strict';
const http2 = require('http2');
const agent = require('http2-wrapper/source/agent');
const ClientRequest = require('http2-wrapper/source/client-request');
const IncomingMessage = require('http2-wrapper/source/incoming-message');
const auto = require('http2-wrapper/source/auto');

const request = (url, options, callback) => {
	return new ClientRequest(url, options, callback);
};

const get = (url, options, callback) => {
	// eslint-disable-next-line unicorn/prevent-abbreviations
	const req = new ClientRequest(url, options, callback);
	req.end();

	return req;
};

module.exports = {
	...http2,
	ClientRequest,
	IncomingMessage,
	...agent,
	request,
	get,
	auto
};
